function setReCaptchaSiteKey() {
	var keyDiv = $('.g-recaptcha');

	if (!isEmpty(keyDiv) && !isEmpty(RECAPTCHA_SITE_KEY)) {
		keyDiv.attr("data-sitekey", RECAPTCHA_SITE_KEY);
	}
}

function fnGetStickerCategorySelected() {
	var code = "";

	// Retrieve the code from cookie
	code = fnGetCookie("STICKER_CATEGORY_CODE");

	// if not exist in the cookie, retrieve from database marked with default
	if(isEmpty(code)) {
		var codeList = fnGetCodeList("STICKER_CATEGORY", {"status":"1", "codeExtra1":"def"});

		if(!isEmpty(codeList)) {
			code = codeList[0].code;
		}

		fnSetStickerCategorySelected(code);
	}

	return code;
}

function fnSetStickerCategorySelected(code) {
	// Set the code in cookie
	if(!isEmpty(code)) {
		fnSetCookie("STICKER_CATEGORY_CODE", code);
	}
}

function fnDisplayStickerImageSelected($target) {

	if(!$target) {
		$target = $("body");
	}

	var stickerCategory = fnGetStickerCategorySelected();

	if(isEmpty(stickerCategory)) {
		return;
	}

	// Hide all the sticker images
	var $cms_sticker_info_list = $target.find(".cms_sticker_info");
	$cms_sticker_info_list.addClass("hidden");

	// Show the sticker image selected
	$target.find(".cms_sticker_info." + stickerCategory).removeClass("hidden");

	$("body").find(".sticker_category").removeClass("hidden");
	$("body").find(".sticker_category_active").addClass("hidden");

	$("body").find(".sticker_category." + stickerCategory).addClass("hidden");
	$("body").find(".sticker_category_active." + stickerCategory).removeClass("hidden");
}

