Array.prototype.orderby = function(sortField, sortType) {
	if (!Array.isArray(this) || !this.valueOf()) return [];

	var arr = this.slice();

	if(!sortType) {
		sortType = "D";
	}

	//Check input validation
	if(isEmpty(sortField) || $.inArray(sortType, ["D", "DSC", "DESC", "A", "ASC"]) == -1) {
		return arr
	}

	sortType = sortType.toUpperCase();

	if(sortField) {
		if(sortType == "D" || sortType == "DSC" || sortType == "DESC") {
			arr = arr.sort(function(a, b){ return a[sortField] > b[sortField] ? -1 : a[sortField] < b[sortField] ? 1 : 0; });
		} else if(sortType == "A" || sortType == "ASC") {
			arr = arr.sort(function(a, b){ return a[sortField] < b[sortField] ? -1 : a[sortField] > b[sortField] ? 1 : 0; });
		}
	}

	return arr;
};

function fnGetDataList(dataList, params, args) {
	var resultArray = [];

	if(dataList) {
		if(params) {
			for(var i=0 ; i < dataList.length ; i++) {
				var data = dataList[i];
				var is_matched = true;

				for(var key in params) {
					if(data[key] == undefined) {
						//조건 불일치
						is_matched = is_matched & false;
					} else if(data[key] !== undefined && params[key] !== data[key]) {
						//조건 불일치
						is_matched = is_matched & false;
					} else {
						//조건 일치
						is_matched = is_matched & true;
					}
				}

				if(is_matched) {
					resultArray.push(data);
				}
			}

		} else {
			resultArray = dataList;
		}

		if(args && args.length > 0) {
			for(var i=0 ; i < resultArray.length ; i++) {
				var data = resultArray[i];
				var selectedColumns = {};

				for(var j=0 ; j < args.length ; j++) {
					selectedColumns[args[j]] = data[args[j]];
				}
				//resultArray.push(selectedColumns);
				resultArray.splice(i, 1, selectedColumns);
			}
		}
	}

	return resultArray;
}

function fnGetDataOne(dataList, params, fieldName, sortField) {
	var result = {};

	if(dataList) {
		//sort
		if(sortField) {
			dataList = dataList.sort(function(a, b){ return a[sortField] > b[sortField] ? -1 : a[sortField] < b[sortField] ? 1 : 0; });
		}

		//param 조건 예시
		//{"ccd_campus" : "CPS01", "is_display" : "1"}
		if(params) {
			for(var i=0 ; i < dataList.length ; i++) {
				var data = dataList[i];
				var is_matched = true;

				for(var key in params) {
					if(data[key] == undefined) {
						//조건 불일치
						is_matched = is_matched & false;
					} else if(data[key] !== undefined && params[key] !== data[key]) {
						//조건 불일치
						is_matched = is_matched & false;
					} else {
						//조건 일치
						is_matched = is_matched & true;
					}
				}

				if(is_matched) {
					result = data;
					break;
				}
			}

		} else {
			result = dataList[0];
		}

		if(fieldName) {
			result = result[fieldName];
		}
	}

	return result;
}